import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HighlightIcon from '@material-ui/icons/Highlight';
import MessageIcon from '@material-ui/icons/Message';
import AssignmentIcon from '@material-ui/icons/Assignment';
import CalendarTodayIcon from '@material-ui/icons/CalendarToday';
import MenuIcon from '@material-ui/icons/Menu';
import {Link} from 'react-router-dom';

const styles = {
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
};

class SideMenu extends React.Component {
  constructor(props){
    super(props);
  this.state = {
    left: false,
    heading: ""
  }};

  toggleDrawer = (side, open) => () => {
    this.setState({
      [side]: open,
    });
  };

 setHeadingInMenuBar(headingString){
   return headingString;
  }

  render() {
    const { classes } = this.props;

    const sideList = (
      <div>
        <List>
            <ListItem onClick={this.setHeadingInMenuBar("Lights")} component={Link} to='../lightsSettings' button>
              <ListItemIcon><HighlightIcon/></ListItemIcon>
              <ListItemText primary="Lights" />
            </ListItem>
            <ListItem onClick={this.setHeadingInMenuBar("Chat")} component={Link} to='../chat' button>
              <ListItemIcon><MessageIcon/></ListItemIcon>
              <ListItemText primary="Chat" />
            </ListItem>
            <ListItem onClick={this.setHeadingInMenuBar("Todos")} component={Link} to='../todos' button>
              <ListItemIcon><AssignmentIcon/></ListItemIcon>
              <ListItemText primary="Todos" />
            </ListItem>
            <ListItem onClick={this.setHeadingInMenuBar("Kalender")} component={Link} to='../calendar' button>
              <ListItemIcon><CalendarTodayIcon/></ListItemIcon>
              <ListItemText primary="Kalender" />
            </ListItem>
        </List>

      </div>
    );

    return (
      <div>
        <MenuIcon onClick={this.toggleDrawer('left', true)} />
        <SwipeableDrawer
          open={this.state.left}
          onClose={this.toggleDrawer('left', false)}
          onOpen={this.toggleDrawer('left', true)}
        >
          <div
            tabIndex={0}
            role="button"
            onClick={this.toggleDrawer('left', false)}
            onKeyDown={this.toggleDrawer('left', false)}
          >
            {sideList}
          </div>
        </SwipeableDrawer>
      </div>
    );
  }
}

SideMenu.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SideMenu);
export {setHeadingInMenuBar};
