import React, { Component } from 'react';
import InputBase from '@material-ui/core/InputBase';
import Paper from '@material-ui/core/Paper';
import SendIcon from '@material-ui/icons/Send';
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import Checkbox from '@material-ui/core/Checkbox';
import './chat.css';

class Chat extends Component {
  constructor(props){
    super(props);
    this.state = {
      massage: []
    }
    this.addMassage = this.addMassage.bind(this);
    this.loadMassages = this.loadMassages.bind(this);
    this.timer = null;
  }


   loadMassages() {
       let cardStates = [];
    fetch("http://116.203.42.55/massages")
    .then(res => res.json())
    .then((res) => {
      this.setState({
        massage: res.massageList
      })
    })
      }


   addMassage(e) {
     e.preventDefault();
     e.stopPropagation();
    fetch("http://116.203.42.55/addMassage" , {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      user: "Tim",
      content: this.InputBase.value
    })
  })
    .then(res => res.json())
    .then((res) => {
      this.InputBase.value = "";
      this.loadMassages()
    })
    if (this.InputBase.value === "") {
      return ;
    }
  }


componentDidMount() {
 this.loadMassages();

  this.timer = setInterval(() => {
    this.loadMassages();
  }, 500);
}

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  render() {

    return (
      <div className="Container">
        <div className="messagesContainer">
        {
          this.state.massage.map((e, i) => (
            <Card className={e.checked?  'todoCard bgChecked': 'todoCard'} key={i}>
              <CardContent>
                <Typography variant="subtitle1" color="textSecondary">
                  {e.user}
                </Typography>
                <Typography component="h5" variant="h5">
                  {e.title}
                </Typography>
              </CardContent>
              <CardActions>
                <Button onClick={()=>this.toggleTodo(i)}>
              <Checkbox color="primary" checked={e.checked} onChange={()=>(this.toggleBgGreen(i,e.checked))}/>
              </Button>
              <Button onClick={()=>this.removeTodo(i)}>
                <DeleteOutlineIcon />
            </Button>
            </CardActions>
            </Card>
          ))
        }
        </div>
        <Button  variant="contained" className={deletBtn.join(' ')}  color="secondary"  onClick={()=>this.removeAllCheckedTodos()}>
          <DeleteOutlineIcon />
        </Button>
        <div className="ToDosCreater">
          <Paper className="ToDosInt">
            <form className="formContainer" onSubmit={this.addTodo}>
              <InputBase className="inputField" inputRef={(ref) => {this.InputBase = ref}} defaultValue="" placeholder="Was cooles!"/>
              <Button  variant="contained" className="sendBtn" color="primary" onClick={(e)=>this.addTodo(e)}>
                <SendIcon/>
              </Button>
            </form>
          </Paper>
        </div>
      </div>
    );
  }
}

export default Chat;
