import React from "react";
import purple from "@material-ui/core/colors/purple";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";


class SwitchBtn extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      checkedA: true,
      checkedB: true
    }
  }
  handleChange(toChange){
    console.log(this.state.toChange);
    this.setState.toChange = !toChange;
  }
  handleClick(stateToChange){
    console.log(this.state.toChange);
    this.setState(prevState => ({
  stateToChange: !prevState.stateToChange
}));
  }

render(){
  return (
    <FormGroup row>
      <FormControlLabel
        control={
          <Switch
            checked={this.state.checkedA}
            onChange={this.handleChange("checkedA")}
            value="checkedA"
            onClick={this.handleClick(checkedA)}
          />
        }
        label="Custom color"
      />
      <FormControlLabel
        control={
          <Switch
            disableRipple
            checked={this.state.checkedB}
            onChange={this.handleChange("checkedB")}
            value="checkedB"
          />
        }
        label="iOS style"
      />
    </FormGroup>
  );
}}

export default SwitchBtn;
