import React from "react";
import purple from "@material-ui/core/colors/purple";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";


class SwitchBtn extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      ligth1: true,
      ligth2: false,
      ligth3: false,
      ligth4: false
    }
  }
  handleClick(stateToChange){
console.log("Bin in der Funktion");
    this.setState({
      stateToChange: !stateToChange
});

console.log(this.state.ligth1);
  }

render(){
  return (
    <FormGroup row>
      <FormControlLabel
        control={
          <Switch
            value="light1"
            onClick={() => this.handleClick("ligth1")}
          />
        }
        label={this.state.light1 ? "ON" : "OFF"}
      />
      <FormControlLabel
        control={
          <Switch
            value="checkedB"
          />
        }
        label={this.state.light2 ? "ON" : "OFF"}
      />
    </FormGroup>
  );
}}

export default SwitchBtn;
