import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HighlightIcon from '@material-ui/icons/Highlight';
import MessageIcon from '@material-ui/icons/Message';
import AssignmentIcon from '@material-ui/icons/Assignment';
import CalendarTodayIcon from '@material-ui/icons/CalendarToday';
import MenuIcon from '@material-ui/icons/Menu';
import {Link} from 'react-router-dom';
import LightsSettings from '../lightsSettings/lightsSettings.js';
import Calendar from '../calendar/calendar.js';
import Todos from '../todos/todos.js';
import Chat from '../chat/chat.js';
const styles = {
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
};

class SideMenu extends React.Component {
  state = {
    left: false
  };

  toggleDrawer = (side, open) => () => {
    this.setState({
      [side]: open,
    });
  };

  render() {
    const { classes } = this.props;

    const sideList = (
      <div>
        <List>
            <ListItem component={LightsSettings} to="../lightsSettings/lightsSettings.js" button >
              <ListItemIcon><HighlightIcon/></ListItemIcon>
              <ListItemText primary="Lights" />
            </ListItem>
            <ListItem component={Chat} to="../chat/chat.js" button  >
              <ListItemIcon><MessageIcon/></ListItemIcon>
              <ListItemText primary="Chat" />
            </ListItem>
            <ListItem component={Todos} to="../todos/todos.js" button >
              <ListItemIcon><AssignmentIcon/></ListItemIcon>
              <ListItemText primary="Todos" />
            </ListItem>
            <ListItem component={Calendar} to="../calendar/calendar.js" button >
              <ListItemIcon><CalendarTodayIcon/></ListItemIcon>
              <ListItemText primary="Kalender" />
            </ListItem>
        </List>
        <div className="content">
          <Route path="/lightsSettings" component={LightsSettings}/>
          <Route path="/chat" component={Chat}/>
          <Route path="/todos" component={Todos}/>
          <Route path="/calendar" component={Calendar}/>
        </div>

      </div>
    );

    return (
      <div>
        <MenuIcon onClick={this.toggleDrawer('left', true)} />
        <SwipeableDrawer
          open={this.state.left}
          onClose={this.toggleDrawer('left', false)}
          onOpen={this.toggleDrawer('left', true)}
        >
          <div
            tabIndex={0}
            role="button"
            onClick={this.toggleDrawer('left', false)}
            onKeyDown={this.toggleDrawer('left', false)}
          >
            {sideList}
          </div>
        </SwipeableDrawer>
      </div>
    );
  }
}

SideMenu.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SideMenu);
