import React from "react";
import purple from "@material-ui/core/colors/purple";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";


class SwitchBtn extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      ligth1: true,
      ligth2: true,
      ligth3: true,
      ligth4: true
    }
     this.handleClick = this.handleClick.bind(this);
  }
  handleClick(stateToChange){
console.log("Bin in der Funktion");
    this.setState( () => ({
      stateToChange: !this.state.stateToChange
}));

console.log(this.state.ligth1);
  }

render(){
  return (
    <FormGroup row>
      <FormControlLabel
        control={
          <Switch
            value="light1"
            onClick={() => this.handleClick(this.state.ligth1)}
          />
        }
        label="Custom color"
      />
      <FormControlLabel
        control={
          <Switch
            disableRipple
            checked={this.state.checkedB}
            onChange={this.handleChange("checkedB")}
            value="checkedB"
          />
        }
        label="iOS style"
      />
    </FormGroup>
  );
}}

export default SwitchBtn;
