import React from "react";
import purple from "@material-ui/core/colors/purple";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";


class SwitchBtn extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      lights: []
    }
  }
  loadLights() {
   fetch("116.203.42.55/lights")
   .then(res => res.json())
   .then((res) => {
     console.log(res);
     this.setState({
       lights: res.lightStates
     })
   })
 }

 toggleLights(i) {
  fetch("116.203.42.55/toggleLights" , {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    index: i,
 })
})
  .then(res => res.json())
  .then((res) => {
    console.log(res);
    this.loadLights()
  })
}


  handleClick(e){
    switch (e) {
      case 1:
      this.setState({
        light1: !this.state.light1
      });
        break;
      case 2:
      this.setState({
        light2: !this.state.light2
      });
        break;
      case 3:
      this.setState({
        light3: !this.state.light3
      });
        break;
      case 4:
      this.setState({
        light4: !this.state.light4
      });
        break;
    }
console.log(this.state);
  }
  handleLable(e){
    if (this.state.e) {
      return "ON";
    }
    else {
      return "OFF"
    }
  }

render(){
  return (
    <FormGroup row>
      <FormControlLabel
        control={
          <Switch
            value="light1"
            onClick={() => this.handleClick(1)}
          />
        }
        label={this.handleLable(1)}
      />
      <FormControlLabel
        control={
          <Switch
            value="light2"
            onClick={() => this.handleClick(2)}
          />
        }
        label={this.handleLable(2)}
      />
    </FormGroup>
  );
}}

export default SwitchBtn;
