import React, { Component } from 'react';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import SendIcon from '@material-ui/icons/Send';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import './todos.css';
class Todos extends Component {
  constructor(props){
    super(props);
    this.state = {
      todos: [],
    }
  }
   loadTodos() {
    fetch("http://116.203.42.55/todos")
    .then(res => res.json())
    .then((res) => {
      console.log(res);
      this.setState({
        todos: res.list
      })
    })
  }

   addTodo() {
    fetch("http://116.203.42.55/add" , {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      user: "Tim",
      title: this.input.value
    })
  })
    .then(res => res.json())
    .then((res) => {
      console.log(res);
      this.input.value = "";
      this.loadTodos()
    })
  }
  toggleTodo(i) {
   fetch("http://116.203.42.55/toggleTodo" , {
   method: 'POST',
   headers: {
     'Accept': 'application/json',
     'Content-Type': 'application/json'
   },
   body: JSON.stringify({
     index: i,
  })
 })
   .then(res => res.json())
   .then((res) => {
     console.log(res);
     this.loadTodos()
   })
 }
 removeTodo(i) {
  fetch("http://116.203.42.55/remove" , {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    index: i,
 })
})
  .then(res => res.json())
  .then((res) => {
    console.log(res);
    this.loadTodos()
  })
}
   componentDidMount() {
    this.loadTodos();

    setInterval(() => {
      this.loadTodos();
    }, 500);
  }
  render() {
    return (
      <div className="ToDosContainer">
        <Paper className="ToDosCreater">
            <InputBase ref={ref=>this.input=ref} defaultValue="" placeholder="Was cooles!"/>
              <Button variant="contained" color="primary" onClick={()=>this.addTodo()}>
                <SendIcon/>
              </Button>
        </Paper>
        {
          this.state.todos.map((e, i) => (
            <Card className="todoCard" key={i}>
              <p>{e.title} || {e.user}</p><input type="checkbox" checked={e.checked} onClick={()=>this.toggleTodo(i)}/>
              <button onClick={()=>this.removeTodo(i)}>löschen</button>
            </Card>
          ))
        }
      </div>
    );
  }
}

export default Todos;
